def find_subarray_sum(arr, target):
    seen_sums = {0} # Set to track cumulative sums
    current_sum = 0

    for num in arr:
        current_sum += num
        if (current_sum - target) in seen_sums:
            return True
        seen_sums.add(current_sum)
    return False

